import { createSlice } from "@reduxjs/toolkit";

// 장바구니 redux
const cart = createSlice({
  name: "장바구니",
  initialState: [],
  reducers: {
    추가하기: (state, action) => {
      state.push(action.payload); // 빈 배열이었던 state에 데이터가 들어감
    },
  },
});

export const { 추가하기 } = cart.actions;
export default cart;
