import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { 추가하기 } from "../index.js";

export default function Product() {
  const products = useSelector((state) => state.상품들);
  const dispatch = useDispatch((state) => state.카트);
  const { id } = useParams();
  // console.log(id);
  const product = products.find((item) => item.id === id); // 6라인의 products임
  const [selected, setSelected] = useState("");
  return (
    <>
      <div>
        <h1>상세 페이지</h1>
        <div
          style={{
            display: "flex",
            gap: "44px",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              width: "200px",
              height: "240px",
              backgroundColor: "#068FFF",
            }}
          >
            {product.name}
          </div>
          <div>
            <h3>가격: {product.price}</h3>
            <h3>좋아요: {product.likes}</h3>
            <h3>구매옵션</h3>
            <select
              style={{
                width: "100px",
              }}
              onChange={(e) => {
                setSelected(e.target.value);
              }}
            >
              <option value="">선택하세요</option>
              {product.options.map((option) => {
                return (
                  <option key={option} value={option}>
                    {option}
                  </option>
                );
              })}
            </select>
            <h6>구매옵션 : {selected}</h6>
            <button
              onChange={() => {
                dispatch(추가하기(product));
                // 클릭을 한 product가 index.js의 추가하기의 action.payload로 들어감
              }}
            >
              장바구니 담기
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
