import { useState } from "react";

function App() {
  const [count, setCount] = useState(0);
  return (
    <div className="App">
      <Box count={count} setCount={setCount} />
    </div>
  );
}

function Box({ count, setCount }) {
  return (
    <div>
      <span>숫자</span>
      <span>{count}</span>
      <Button setCount={setCount} />
    </div>
  );
}

function Button({ setCount }) {
  return (
    <button
      onClick={() => {
        setCount((count) => count + 1);
      }}
    >
      숫자 올리기
    </button>
  );
}

export default App;
